<script>
import HomeHeader from "pages/home/HomeHeader";
import ArtistProject from "pages/home/ArtistProject";
import Swiper from "swiper";
import { scroll } from "utils/scroll";
// import moreProTop from "pages/home/MoreProTop";
import moreProList from "pages/home/MoreProList";
import http from "utils/http";
import { Indicator } from "mint-ui";
import _ from "lodash";
export default {
  components: {
    HomeHeader,
    ArtistProject,
    // moreProTop,
    moreProList
  },
  methods: {
    sortby: function() {
      this.parentMsg = _.sortBy(this.parentMsg, ["venueId"]);
      this.isActive = 2;
    },
    sortbyTotal: function() {
      
      this.parentMsg = this.originMsg;
      this.isActive = 0;
    },
    sortbyTime: function() {
      this.parentMsg = _.sortBy(this.parentMsg, ["nearestPerformTime"]);
      this.isActive = 1;
    },
    sortPrice: function() {
      this.parentMsg = _.sortBy(this.parentMsg, ["priceStr"]);
      this.isActive = 3;
    }
  },
  data() {
    return {
      parentMsg: [],
      originMsg: [],
      isActive: 2
    };
  },
  async mounted() {
    // console.log(this.isActive)
    new Swiper(".swiper-container", {
      loop: true, // 循环模式选项
      // 如果需要分页器
      pagination: {
        el: ".swiper-pagination"
      },
      //自动切换开启，默认停留三秒
      autoplay: true
    });
    Indicator.open({
      text: "Loading...",
      spinnerType: "fading-circle"
    });
    let result = await http({
      method: "get",
      url: "/h5/projectinfo"
    });
    Indicator.close();
    this.parentMsg = _.sortBy(result.data.data.projectInfo, ["venueId"]);
    this.originMsg = result.data.data.projectInfo;
    this.$nextTick(() => {
      scroll({
        el: "#mainwrap",
        url: "/h5/artistMore",
        count: 5,
        maindata: this.parentMsg,
        vm: this
      });
    });
  }
};
</script>
